import React, {useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';

function UpdateJob(){

const [Jobs, SetJobs] = useState([]);

    useEffect(() => {
        const api = "http://localhost:2000/";
        fetch(api)
          .then((response) => response.json())
          .then((data) => {
            const jobsData = data.Jobs;
            const singleJob=jobsData.filter


           SetJobs(jobsData);
          })
          .catch((e) => {
            console.error(e.message);
          });
      }, []);
    const { Jobid } = useParams();
console.log(Jobid)

  return (
   
        <section className='container-fluid '>
        <div className="form-wraper">
        <div className="header">
          
            <h5>Update job</h5>
        </div>
        <form action='http://localhost:3003/PostJob' method='post'>
    
            <div className="row">
                <div className="col-25"><label htmlFor="fname">Job title:</label></div>
                <div className="col-75"> <input type="text" id="Jtitle" name="Jtitle" autoFocus/></div>
            </div>
            <div className="row">
                <div className="col-25"><label htmlFor="lname">Company:</label></div>
                <div className="col-75"><input type="text" id="Company" name="Company"/></div>
            </div>
            <div className="row">
                <div className="col-25"><label htmlFor="lname">Location:</label></div>
                <div className="col-75"><input type="text" id="Location" name="Location"/></div>
            </div>
            <div className="row">
                <div className="col-25"><label htmlFor="lname">Salary:</label></div>
                <div className="col-75"><input type="text" id="Salary" name="Salary"/></div>
            </div>
            <div className="row">
                <div className="col-25"><label htmlFor="lname">Describtion:</label></div>
                <div className="col-75"><input type="text" id="Describtion" name="Describtion"/></div>
            </div>
            
           
                <div className="row">
                    <div className="col-75">
                        <div className="btn-container">
                            <input type="submit" value="Save Update"/>
                        </div>
                    </div>
                </div>
    
        </form>
    </div>
     
        </section>
    
  )
}

export default UpdateJob
