import React, { useState } from "react";
import axios from "axios";

function PostJob() {
  const [values, setValues] = useState({
    Company: "",
    Description: "",
    Jtitle: "",
    Location: "",
    Salary: "",
  });

  const [message,setMessage]=useState("")
  const [error,setError]=useState(false)
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: [e.target.value] });
  };

 
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:2000/PostJob", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(values)
      });
  
      const data = await response.json(); // Parse the response
  
      if (response.ok) {
        setMessage(data.message); // || "Job posted successfully!"
        setError(false); // Reset error if the request is successful
      } else {
        setError(true);
        setMessage(data.message || "An error occurred. Please try again."); // Handle the error message
      }
    } catch (err) {
      setError(true);
      setMessage("An unexpected error occurred. Please try again.");
      console.error(err);
    }
  };
  


  return (
    <div className="container bg-light shadow">
      <h2>Post a Job</h2>
      <form onSubmit={handleSubmit} className="row col-md-5"  method="post">
        <div>
          <label>Company:</label>
          <input
            className="form-control"
            type="text"
            name="Company"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            className="form-control"
            type="text"
            name="Description"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Job Title:</label>
          <input
            className="form-control"
            type="text"
            name="Jtitle"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            className="form-control"
            type="text"
            name="Location"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Salary:</label>
          <input
            className="form-control"
            type="number"
            name="Salary"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mt-4">
          <input className="btn btn-primary form-control" type="submit" value="Register"/>
            
          
        </div>
      </form>

      {/* Display success or error message */}
      {message && <p style={{ color: error ? "red" : "green" }}>{message}</p>}
    </div>
  );
}

export default PostJob;
