import React, { useState,useEffect } from 'react'
import { Link } from 'react-router-dom'
import Fouro4 from '../../ErorrPage/Fouro4';

function ManageJob() {
// const [Jobs,setJobs]=useState([])
const [Jobs, SetJobs] = useState([]);

    useEffect(() => {
        const api = "http://localhost:2000/";
        fetch(api)
          .then((response) => response.json())
          .then((data) => {
            const jobsData = data.Jobs;
           SetJobs(jobsData);
          })
          .catch((e) => {
            console.error(e.message);
          });
      }, []);
     let count=1;
  
      console.log(Jobs);
if(Jobs.length){
  return (
    <div className='container-fluid  pt-5 ' style={{marginTop:"100px"}}>
   <div style={{float:'right'}} className='d-flex'><h2> <Link to='../PostJob' type="button" className="btn btn-primary" data-bs-toggle="button">Add New job</Link></h2></div>     
<table className="table table-striped">
  <thead>
    <tr>
      <th scope="col">No</th>
      <th scope="col">Job ID</th>
      <th scope="col">Job title</th>
      <th scope="col">Company</th>
      <th scope="col">Location</th>
      <th scope="col">Salary</th>
      <th scope="col">Description</th>
      <th scope="col " className='text-center' colSpan="2" >Action</th>
    </tr>
  </thead>
  <tbody className="table-group-divider">
  { 
  
  Jobs.map((items,key) =>{
         let Result=(<tr key={key}>
            <td>{count}</td>
            <td>{items.JOB_ID}</td>
            <td>{items.JOB_TITLE}</td>
            <td>{items.COMPANY}</td>
            <td>{items.LOCATION}</td>
            <td>{items.SALARY}</td>
            <td>{items.DESCRIPTION}</td>
            <td><Link className='btn btn-success'>Update</Link></td>
           <td><Link className='btn btn-danger'>Delete</Link></td>        
          </tr>)
          return Result;

          
         }   
   )}
   
   
   
  </tbody>
</table>

    </div>
  );

}

else {return <div className='container-fluid  pt-5 ' style={{marginTop:"200px"}}></div>} 
}

export default ManageJob
