import React, { useState } from 'react';

const SendSMS = () => {
  const [smsData, setSmsData] = useState({
    from:'',
    to: '',
    message: ''
  });
  const [responseMessage, setResponseMessage] = useState('');
  const [sending, setSending]=useState(false)
  const handleChange = (e) => {
    setSmsData({
      ...smsData,
      [e.target.name]: e.target.value
    });
  };

  const sendSMS = async (e) => {
    e.preventDefault();
    setSending(true)
    try {
      const response = await fetch('http://localhost:2000/api/send-sms', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(smsData)
      });
      
      const data = await response.json();
      if (data.success) {
        setResponseMessage('SMS sent successfully!');
        setSending(false)
      } else {
        setResponseMessage(`Failed to send SMS: ${data.error}`);
        setSending(false)
      }
    } catch (error) {
      setResponseMessage(`Error sending SMS: ${error.message}`);
      setSending(false)
    }
  };

  return (
    <div className="container">
      <h2>Send SMS</h2>
      {responseMessage && <p>{responseMessage}</p>}
      <form onSubmit={sendSMS}>
      <div className='my-3'>
          <label>From:</label>
          <input 
            className='form-control'
            type="text"
            name="from"
            placeholder="+1234567890"
            value={smsData.From}
            onChange={handleChange}
            required
          />
        </div>
      <div className='my-3'>
          <label>To:</label>
          <input 
            className='form-control'
            type="text"
            name="to"
            placeholder="+1234567890"
            value={smsData.to}
            onChange={handleChange}
            required
          />
        </div>
        <div className='my-3'>
          <label>Message:</label>
          <textarea
            className='form-control'
            name="message"
            placeholder="Enter your message here"
            value={smsData.message}
            onChange={handleChange}
            required
          />
        </div>
        <button className='form-control btn btn-primary' type="submit">  {sending?"Sending":"Send SMS"}</button>
      </form>
    </div>
  );
};

export default SendSMS;
