import React,{useState,useEffect} from 'react'
import { Link } from 'react-router-dom';


    
const Documents = () => {
    const [file, setFile] = useState(null);
    const [documentData, setDucumentdata] = useState({});
    const [err,setError]=useState("")
    const [message,setMessage]=useState("")
  
    useEffect(() => {
      const LoadDocumentData = async () => {
        try {
          const response = await fetch("http://localhost:2000/documents");
          const data = await response.json();
          if(response.ok){
            setDucumentdata(data.Documents)
            console.log(data.Documents)
         //  console.log(documentData)
          }
         
        } catch (err) {
          console.log(err);
        }
      };
  
      LoadDocumentData();
    }, []);
  
    const handleFileChange = (e) => {
      setFile(e.target.files[0]);
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      setError("")
      setMessage("")
      if(!file)
      {
        setError("Please select file")
      }
      const formData = new FormData();
      formData.append("myDocument", file);
  
      try {
        const response = await fetch("http://localhost:2000/documents/upload", {
          method: "POST",
          body: formData,
        });
        const data = await response.json();
  //console.log("status",response.status)
        if (response.status===200) {
       
          // Re-fetch the image data to update the displayed image
        //   const updatedResponse = await fetch("http://localhost:2000/documents");
        //   const updatedData = await updatedResponse.json();
        // //   setDucumentdata(updatedData);
          setMessage(data.message)
          
          
        } else {
          console.log("An error occurred.");
          setError("An error occurred.");
        }
      } catch (err) {
        setError("An unexpected error occurred.");
        console.error(err);
      }
    };
    function handleDownload(url){
        fetch(url)
   }
  return (
    <div className="container-fluid login-bg">
      <div className="container">
      {message&&<p style={{color:"black"}}>{message}</p>}
    {err&&<p style={{color:"red"}}>{err}</p>}
      <form onSubmit={handleSubmit}>
        <label htmlFor="doc">Choose file:</label>
        <div className='my-3 d-flex col-6 '>
        <input className='form-control' type="file" name="doc" onChange={handleFileChange} />
        <button className=' btn btn-success' type="submit">Upload</button>
        </div>
      </form>
   
      <table className="table table-striped table-hover">
            <thead style={{backgroundColor:"lightgray"}}>
              <tr>
                <th scope="col">No</th>
                <th scope="col">Doc ID</th>
                <th scope="col">filename</th>    
                <th style={{textAlign:"center"}} colspan={1}>Action</th>              
              </tr>
            </thead>
            <tbody className="table-group-divider">
              {documentData && documentData.length > 0 ? (
                documentData.map((item, index) =>
                   
                  item ? (
                    <tr key={item.Documents.id}>
                      <td>{index+1}</td>
                      {/* <td>{item.id}</td> */}
                      <td><a style={{textDecoration:"underline",color:"blue"}} href='#'>{item.Documents}</a></td>
                     
                      <td style={{ display: "Flex", columnGap: "10px" }}>
                        <Link onClick={handleDownload(item.d)} className="btn btn-success">Download</Link>
                        <button
                         
                          className="btn btn-warning"
                        >
                          Update
                        </button>
                        <Link
                        
                          className="btn btn-danger"
                        >
                          Delete
                        </Link>
                      </td>
                    </tr>
                  ) : null
                )
              ) : (
                <tr>
                  <td colSpan="8" className="text-center">
                    No file
                  </td>
                </tr>
              )}
            </tbody>
          </table>

      </div>
    </div>
  )
}

export default Documents
