import React, { useState,useEffect } from 'react'
import { Link } from 'react-router-dom'

const ManageJob = () => {
const [Jobs,setJobs]=useState([])

    useEffect(() => {
        const api = "http://localhost:3003/";
        fetch(api)
          .then((response) => response.json())
          .then((data) => {
            const jobsData = data;
            setJobs(jobsData);
          })
          .catch((e) => {
            console.error(e.message);
          });
      }, []);
      let count=1;
      console.log(Jobs);
if(Jobs){
  return (
    <div className='container-fluid  pt-5 ' style={{marginTop:"100px"}}>
   <div style={{float:'right'}} className='d-flex'><h2> <Link to='../PostJob' type="button" className="btn btn-primary" data-bs-toggle="button">Add New job</Link></h2></div>     
<table className="table table-striped">
  <thead>
    <tr>
      <th scope="col">No</th>
      <th scope="col">Job ID</th>
      <th scope="col">Company</th>
      <th scope="col">Location</th>
      <th scope="col">Salary</th>
      <th scope="col">Description</th>
      <th scope="col " className='text-center' colspan="2" >Action</th>
    </tr>
  </thead>
  <tbody className="table-group-divider">
  { 
  
  Jobs.map((items) => (
            <tr key={items.JOB_ID}>
             <td>{count}</td>
              <td>{items.JOB_ID}</td>
              <td>{item.COMPANY}</td>
              <td>{item.LOCATION}</td>
              <td>{item.DESCRIPTION}</td>
              <td>{item.SALARY}</td>
              
            </tr>
   ))}
    <tr>
      
      <td>1</td>
      <td>56</td>
      <td>CBE</td>
      <td>@Robe</td>
      <td>3000</td>
      <td>Descry</td>
      <td><Link className='btn btn-success'>Update</Link></td>
      <td><Link className='btn btn-danger'>Delete</Link></td>
    </tr>
   
   
  </tbody>
</table>
    </div>
  )

}

else return <h1>No jobs </h1>
}

export default ManageJob
