import React, { useState } from 'react';

const SendEmail = () => {
  const [emailData, setEmailData] = useState({
    to: '',
    subject: '',
    text: '',
    html: '',
  });

  const handleChange = (e) => {
    setEmailData({
      ...emailData,
      [e.target.name]: e.target.value,
    });
  };

  const sendEmail = async () => {
    try {
      const response = await fetch('http://localhost:2000/api/send-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(emailData),
      });

      const data = await response.json();
      if (response.ok) {
        alert('Email sent successfully');
      } else {
        console.error(data.message);
      }
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <div className='container p-5'>
      <form action={sendEmail}>
     <div className='my-3'> <input className='form-control' type="email" name="to" placeholder="Recipient" onChange={handleChange} /></div>
     <div className='my-3' ><input className='form-control' type="text" name="subject" placeholder="Subject" onChange={handleChange} /></div>
     <div className='my-3'><textarea className='form-control' name="text" placeholder="Text content" onChange={handleChange} /></div>
     <div className='my-3'><textarea className='form-control' name="html" placeholder="HTML content" onChange={handleChange} /></div>
     <div className='my-3'><button className='form-control bt btn-success' type='submit'>Send Email</button></div>
 


      </form>
     
     
        </div>
  );
};

export default SendEmail;
