import React, { useState,useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Modal, Button } from "react-bootstrap";


function ManageJob() {
  const [showModal, setShow] = useState(false);
  const [modaltype, setModalType] = useState("create");
  const [selectedJob, setSelectedJob] = useState({
    Company: "",
    Description: "",
    Jtitle: "",
    Location: "",
    Salary: "",
  });


  const handleClose = () => {
    setSelectedJob({})
    setShow(false);
  }
  
  const ShowPostModal = () => 
    { setShow(true);
      setModalType("create")
    

    }
  const ShowUpdateModal = (items) => 
    {setShow(true);
      setModalType("update")
      setSelectedJob(items)

    }


  //loading data from server
const [Jobs, SetJobs] = useState([]);
    useEffect(() => {
        const api = "http://localhost:2000/";
        fetch(api)
          .then((response) => response.json())
          .then((data) => {
            const jobsData = data.Jobs;
           SetJobs(jobsData);
          })
          .catch((e) => {
            console.error(e.message);
          });
      }, []);
     let count=1;
  
      console.log(Jobs);














      const [values, setValues] = useState({
        Company: "",
        Description: "",
        Jtitle: "",
        Location: "",
        Salary: "",
      });
    
      const [errors, setErrors] = useState({});
      const [message, setMessage] = useState("");
      const [err, setError] = useState(false);
    
      // Handle form input changes
      const handleChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
      };
    
      // Validate the form fields
      const validate = () => {
        let errors = {};
        if (!values.Company.trim()) {
          errors.Company = "Company name is required.";
        }
        if (!values.Description.trim()) {
          errors.Description = "Description is required.";
        }
        if (!values.Jtitle.trim()) {
          errors.Jtitle = "Job title is required.";
        }
        if (!values.Location.trim()) {
          errors.Location = "Location is required.";
        }
        if (!values.Salary || isNaN(values.Salary) || values.Salary <= 0) {
          errors.Salary = "Salary must be a positive number.";
        }
        return errors;
      };
    
      // Handle form submission
      const handleSubmit = async (e) => {
        e.preventDefault();
        const validationErrors = validate();
        
        setErrors(validationErrors);     
    
        if (Object.keys(validationErrors).length === 0) {
          // No validation errors, proceed to submit the form
          try {
            const response = await fetch("http://localhost:2000/PostJob", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify(values),
            });
    
            const data = await response.json();
            if (response.ok) {
              setMessage("Job posted successfully!");
              setError(false);
            } else {
              setMessage(data.message || "An error occurred.");
              setError(true);
            }
          } catch (err) {
            setMessage("An unexpected error occurred.");
            setError(true);
            console.error(err);
          }
        } else {
          // There are validation errors
          setMessage("Please fix the errors and submit again.");
          setError(true);
        }
      };









const [DeletedData,setDeletedData]=useState("")
 async function ConfirmDelete(item_id){
  setDeletedData(item_id)
  
}
const handleDelete = async (id) => {
  try {
      await axios.delete(`http://localhost:5000/records/${id}`);
      // Optionally, refresh the records list or remove the deleted record from the UI state
  } catch (error) {
      console.error('Error deleting record:', error);
  }
};

     


if(!Jobs==[]){
  return (
    <div className='container-fluid  pt-5   ' style={{marginTop:"100px"}}>
   <div  className='modal-container  bg-light clearfix'>

   <Button style={{float:'right'}} className="btn btn-success" onClick={ShowPostModal}>
          Add new Job
        </Button>
      
  
        
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
        <Modal.Title>{modaltype === 'create' ? 'Post a Job' : 'Update Job'}</Modal.Title>
       
        </Modal.Header>
        <Modal.Body>

       
      <form onSubmit={handleSubmit} className="">
        <div>
          <label>Company<span style={{color:"red",fontSize:"30px",marginLeft:"1px"}}>*</span>:</label>
          <input
            className="form-control"
            type="text"
            name="Company"
            value={selectedJob?.COMPANY}
            onChange={handleChange}
     
          />
          {errors.Company && <p style={{ color: "red" }}>{errors.Company}</p>}
        </div>

        <div>
          <label>Description:</label>
          <input
            className="form-control"
            type="text"
            name="Description"
            onChange={handleChange}
            value={selectedJob?.DESCRIPTION}
          />
          {errors.Description && (
            <p style={{ color: "red" }}>{errors.Description}</p>
          )}
        </div>

        <div>
          <label>Job Title:</label>
          <input
            className="form-control"
            type="text"
            name="Jtitle"
            onChange={handleChange}
            value={selectedJob?.JOB_TITLE}
          />
          {errors.Jtitle && <p style={{ color: "red" }}>{errors.Jtitle}</p>}
        </div>

        <div>
          <label>Location:</label>
          <input
            className="form-control"
            type="text"
            name="Location"
            onChange={handleChange}
            value={selectedJob?.LOCATION}
          />
          {errors.Location && (
            <p style={{ color: "red" }}>{errors.Location}</p>
          )}
        </div>

        <div>
          <label>Salary:</label>
          <input
            className="form-control"
            type="number"
            name="Salary"
            onChange={handleChange}
            value={selectedJob?.SALARY}
          />
          {errors.Salary && <p style={{ color: "red" }}>{errors.Salary}</p>}
        </div>

        <div className="mt-4">
          <input className="btn btn-primary form-control btn-lg" type="submit" value={modaltype==="create"?"register":"Save update"} />
        </div>
      </form>

      {/* Display success or error message */}
      {message && <p style={{ color: err ? "red" : "green" }}>{message}</p>}


        </Modal.Body>
        <Modal.Footer>
          <Button className='btn btn-danger btn-lg' variant="secondary" onClick={handleClose}>
            Close
          </Button>
         
        </Modal.Footer>
      </Modal>
   </div>     

<div  className='table-container shadow bg-light'>
  
<table className="table table-striped">
  <thead>
    <tr>
      <th scope="col">No</th>
      <th scope="col">Job ID</th>
      <th scope="col">Job title</th>
      <th scope="col">Company</th>
      <th scope="col">Location</th>
      <th scope="col">Salary</th>
      <th scope="col">Description</th>
      <th scope="col " className='text-center' colSpan="2" >Action</th>
    </tr>
  </thead>
  <tbody className="table-group-divider">
  { 
  
  Jobs?.map((items,key) =>{
         let Result=(<tr key={key}>
            <td>{count}</td>
            <td>{items.JOB_ID}</td>
            <td>{items.JOB_TITLE}</td>
            <td>{items.COMPANY}</td>
            <td>{items.LOCATION}</td>
            <td>{items.SALARY}</td>
            <td>{items.DESCRIPTION}</td>
            <td><button onClick={() => ShowUpdateModal(items)} className='btn btn-success'>Update</button></td>
           <td><Link onClick={ConfirmDelete(items)} className='btn btn-danger'>Delete</Link></td>        
          </tr>)
          return Result;

          
         }   
   )}
   
   
   
  </tbody>
</table>
</div>
    </div>
  );

}

else {return <div className='container-fluid bg-light  py-4 text-center ' style={{marginTop:"200px"}}><h1>No data found</h1></div>} 















}







export default ManageJob
