import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState("");
  const [showPassword, setVisibility] = useState(false);
  const navigate = useNavigate();

  // Validate the form fields
  const validate = () => {
    let validationErrors = {};
    if (!email.trim()) {
      validationErrors.Email = "Email is required.";
    }
    if (!password.trim()) {
      validationErrors.Password = "Password is required.";
    }
    return validationErrors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      // No validation errors, proceed to submit the form
      try {
        const response = await fetch("https://localhost:2000/api/login", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          // credentials: "include", // Send cookies with request
          body: JSON.stringify({ email, password }),
        });

        const data = await response.json();

        if (response.ok) {
          alert("Login successful!");
          navigate("/dashboard"); // Redirect to a protected route after login
        } else {
          setLoginError(data.message || "Login failed");
        }
      } catch (err) {
       
        setLoginError("An unexpected error occurred."+err);
        console.error(err);
      }
    } else {
      // There are validation errors
      setMessage("Please fix the errors and submit again.");
    }
  };

  return (
    <div className="container-fluid login-bg">
      <div className="container">
        <div className="card shadow">
          <div className="card-header">
            <h3 className="text-center">
              <strong>Login here</strong>
            </h3>
            {loginError && <p style={{ color: "red" }}>{loginError}</p>}
          </div>
          <div className="Logincard-body py-5">
            <form
              onSubmit={handleSubmit}
              className="row col-md-6 Logincard-body align-self-center"
            >
              <div>
                <label>
                  Email<span style={{ color: "red" }}>*</span>:
                </label>
                <input
                  className="form-control form-control-lg"
                  name="Email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                {errors.Email && <p style={{ color: "red" }}>{errors.Email}</p>}
              </div>

              <div style={{ position: "relative" }}>
                <label>
                  Password<span style={{ color: "red" }}>*</span>:
                </label>
                <input
                  className="form-control form-control-lg"
                  type={showPassword ? "text" : "password"}
                  name="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <button
                  type="button"
                  onClick={() => setVisibility(!showPassword)}
                  style={{
                    position: "absolute",
                    right: "20px",
                    top: "70%",
                    transform: "translateY(-60%)",
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  {showPassword ? "Hide" : "Show"}
                </button>

                {errors.Password && (
                  <p style={{ color: "red" }}>{errors.Password}</p>
                )}
              </div>

              <div className="mt-4">
                <button
                  className="btn-primary form-control form-control-lg"
                  type="submit"
                >
                  Login
                </button>
              </div>
            </form>
            {message && <p style={{ color: "red" }}>{message}</p>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
