import React, { useState } from 'react'
// import './PostJob.css'


function PostJob() {
  const [formData, setFormData] = useState("");

  const [message, setMessage] = useState(''); // To store success or error message
  const [error, setError] = useState(false);  // To store the error state

  // Handle form field changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    // e.preventDefault();

    try {
      const response = await fetch('http://localhost:2000/PostJob', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      
    //   if (response.ok) {
    //     setMessage(result.message);  // Display success message
    //     setError(false);             // Reset error state
    //   } else {
    //     setMessage(result.message);  // Display error message from server
    //     setError(true);
    //   }
    } catch (error) {
      setMessage('An error occurred. Please try again.');  // Display generic error message
      setError(true);
    }
  };

  return (
    <div className="container bg-light shadow">
      <h2>Post a Job</h2>
      <form onSubmit={handleSubmit} className="row col-md-5">
        <div>
          <label>Company:</label>
          <input className="form-control"
            type="text"
            name="Company"
            value={formData.Company}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input className="form-control"
            type="text"
            name="Description"
            value={formData.Description}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Job Title:</label>
          <input className="form-control"
            type="text"
            name="Jtitle"
            value={formData.Jtitle}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location:</label>
          <input className="form-control"
            type="text"
            name="Location"
            value={formData.Location}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Salary:</label>
          <input className="form-control"
            type="number"
            name="Salary"
            value={formData.Salary}
            onChange={handleChange}
            required
          />
        </div>
       <div className='mt-4'> <button className='btn btn-primary form-control' type="submit">Submit</button></div>
      </form>

      {/* Display success or error message */}
      {message && (
        <p style={{ color: error ? 'red' : 'green' }}>{message}</p>
      )}
    </div>
  );
}

export default PostJob;
