import React,{useState,useEffect} from 'react'

const Documents = () => {
    const [file, setFile] = useState(null);
    const [documentData, setDucumentdata] = useState();
    const [err,setError]=useState("")
  
    useEffect(() => {
      const LoadDocumentData = async () => {
        try {
          const response = await fetch("http://localhost:300/");
          const data = await response.json();
          if(response.ok){
            setDucumentdata(data)
           console.log(documentData)
          }
         
        } catch (err) {
          console.log(err);
        }
      };
  
      LoadDocumentData();
    }, []);
  
    const handleFileChange = (e) => {
      setFile(e.target.files[0]);
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      if(!file)
      {
        setError("Please select file")
      }
      const formData = new FormData();
      formData.append("myDocument", file);
  
      try {
        const response = await fetch("http://localhost:300/upload", {
          method: "POST",
          body: formData,
        });
  
        if (response.ok) {
          console.log("Document posted successfully!");
          // Re-fetch the image data to update the displayed image
          const updatedResponse = await fetch("http://localhost:300/");
          const updatedData = await updatedResponse.json();
          setDucumentdata(updatedData);
        } else {
          console.log("An error occurred.");
        }
      } catch (err) {
        console.log("An unexpected error occurred.");
        console.error(err);
      }
    };
  
  return (
    <div className="container-fluid login-bg">
      <div className="container">
    {err&&<p>{err}</p>}
      <form onSubmit={handleSubmit}>
        <label htmlFor="doc">Choose file:</label>
        <div className='my-3 d-flex col-6 '>
        <input className='form-control' type="file" name="doc" onChange={handleFileChange} />
        <button className=' btn btn-success' type="submit">Upload</button>
        </div>
      </form>
      <div>
      {/* {imagedata ? (
        <img src={`http://localhost:300/images/${imagedata.image}`} alt="Uploaded" />
      ) : null} */}
    </div>
      </div>
    </div>
  )
}

export default Documents
