import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';

function ManageJob() {
  const [showModal, setShow] = useState(false);
  const [modalType, setModalType] = useState('');
  const [selectedJob, setSelectedJob] = useState({
    Company: '',
    Description: '',
    Jtitle: '',
    Location: '',
    Salary: '',
  });

  const handleClose = () => {
    setShow(false);
    setSelectedJob({
      Company: '',
      Description: '',
      Jtitle: '',
      Location: '',
      Salary: '',
    });
  };

  const ShowPostModal = () => {
    setShow(true);
    setModalType('create');
  };

  const ShowUpdateModal = (job) => {
    setShow(true);
    setModalType('update');
    setSelectedJob(job);
  };

  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch('http://localhost:2000/')
      .then((response) => response.json())
      .then((data) => setJobs(data.Jobs))
      .catch((e) => console.error(e.message));
  }, []);

  const handleChange = (e) => {
    setSelectedJob({ ...selectedJob, [e.target.name]: e.target.value });
  };

  const validate = () => {
    const errors = {};
    if (!selectedJob.Company.trim()) errors.Company = 'Company name is required.';
    if (!selectedJob.Description.trim()) errors.Description = 'Description is required.';
    if (!selectedJob.Jtitle.trim()) errors.Jtitle = 'Job title is required.';
    if (!selectedJob.Location.trim()) errors.Location = 'Location is required.';
    if (!selectedJob.Salary || isNaN(selectedJob.Salary) || selectedJob.Salary <= 0)
      errors.Salary = 'Salary must be a positive number.';
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();

    if (Object.keys(validationErrors).length === 0) {
      try {
        const url = modalType === 'create'
          ? 'http://localhost:2000/PostJob'
          : `http://localhost:2000/UpdateJob/${selectedJob.JOB_ID}`;
        
        const method = modalType === 'create' ? 'POST' : 'PUT';

        const response = await fetch(url, {
          method: method,
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(selectedJob),
        });

        if (response.ok) {
          setJobs([...jobs]); // Optionally refresh job list after update/post
          setShow(false);
        } else {
          console.error('Error occurred.');
        }
      } catch (err) {
        console.error(err);
      }
    }
  };

  if (!jobs.length) {
    return <div>No data found</div>;
  }

  return (
    <div className="container-fluid pt-5" style={{ marginTop: '100px' }}>
      <Button className="btn btn-success" onClick={ShowPostModal} style={{ float: 'right' }}>
        Add new Job
      </Button>

      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{modalType === 'create' ? 'Post a Job' : 'Update Job'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Company:</label>
              <input
                className="form-control"
                type="text"
                name="Company"
                value={selectedJob.Company}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                className="form-control"
                type="text"
                name="Description"
                value={selectedJob.Description}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Job Title:</label>
              <input
                className="form-control"
                type="text"
                name="Jtitle"
                value={selectedJob.Jtitle}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Location:</label>
              <input
                className="form-control"
                type="text"
                name="Location"
                value={selectedJob.Location}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Salary:</label>
              <input
                className="form-control"
                type="number"
                name="Salary"
                value={selectedJob.Salary}
                onChange={handleChange}
              />
            </div>
            <div className="mt-4">
              <input
                className="btn btn-primary form-control"
                type="submit"
                value={modalType === 'create' ? 'Register' : 'Save Update'}
              />
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>Close</Button>
        </Modal.Footer>
      </Modal>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>No</th>
            <th>Job ID</th>
            <th>Job Title</th>
            <th>Company</th>
            <th>Location</th>
            <th>Salary</th>
            <th>Description</th>
            <th className="text-center" colSpan="2">Action</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map((job, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{job.JOB_ID}</td>
              <td>{job.JOB_TITLE}</td>
              <td>{job.COMPANY}</td>
              <td>{job.LOCATION}</td>
              <td>{job.SALARY}</td>
              <td>{job.DESCRIPTION}</td>
              <td>
                <button onClick={() => ShowUpdateModal(job)} className="btn btn-success">Update</button>
              </td>
              <td>
                <Link className="btn btn-danger">Delete</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ManageJob;
