import React, { useState } from "react";
import axios from "axios";

function PostJob() {
  const [values, setValues] = useState({
    Company: "",
    Description: "",
    Jtitle: "",
    Location: "",
    Salary: "",
  });

  const [message,setMessage]=useState("")
  const [err,setError]=useState("")
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: [e.target.value] });
  };

 
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
    const response =fetch("http://localhost:2000/PostJob", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(values)
    })
  
  }
    catch{
      SpeechSynthesisErrorEvent()
    }
     
  }




  return (
    <div className="container bg-light shadow">
      <h2>Post a Job</h2>
      <form onSubmit={handleSubmit} className="row col-md-5"  method="post">
        <div>
          <label>Company:</label>
          <input
            className="form-control"
            type="text"
            name="Company"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            className="form-control"
            type="text"
            name="Description"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Job Title:</label>
          <input
            className="form-control"
            type="text"
            name="Jtitle"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            className="form-control"
            type="text"
            name="Location"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Salary:</label>
          <input
            className="form-control"
            type="number"
            name="Salary"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mt-4">
          <input className="btn btn-primary form-control" type="submit" value="Register"/>
            
          
        </div>
      </form>

      {/* Display success or error message */}
      {/* {message && <p style={{ color: error ? "red" : "green" }}>{message}</p>} */}
    </div>
  );
}

export default PostJob;





