    import React from "react";

const ConfirmDeleteModal = ({ isOpen, onConfirm, onCancel }) => {
  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <h4>Confirm Deletion</h4>
        <p>Are you sure you want to delete this job?</p>
        <button onClick={onConfirm}>confirm, Deleting</button>
        <button onClick={onCancel}>Cancel</button>
      </div>











      <Modal show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>
                {modalType === "create" ? "Post a Job" : "Update Job"}
                {selectedJob.JOB_ID}
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={handleSubmit}>
                <div>
                  <label>
                    Company
                    <span
                      style={{
                        color: "red",
                        fontSize: "30px",
                        marginLeft: "1px",
                      }}
                    >
                      *
                    </span>
                    :
                  </label>
                  <input
                    className="form-control"
                    type="text"
                    name="Company"
                    value={values.Company}
                    onChange={handleChange}
                  />
                  {errors.Company && (
                    <p style={{ color: "red" }}>{errors.Company}</p>
                  )}
                </div>
                <div>
                  <label>Description:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Description"
                    onChange={handleChange}
                    value={values.Description}
                  />
                  {errors.Description && (
                    <p style={{ color: "red" }}>{errors.Description}</p>
                  )}
                </div>
                <div>
                  <label>Job Title:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Jtitle"
                    onChange={handleChange}
                    value={values.Jtitle}
                  />
                  {errors.Jtitle && (
                    <p style={{ color: "red" }}>{errors.Jtitle}</p>
                  )}
                </div>
                <div>
                  <label>Location:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Location"
                    onChange={handleChange}
                    value={values.Location}
                  />
                  {errors.Location && (
                    <p style={{ color: "red" }}>{errors.Location}</p>
                  )}
                </div>
                <div>
                  <label>Salary:</label>
                  <input
                    className="form-control"
                    type="number"
                    name="Salary"
                    onChange={handleChange}
                    value={values.Salary}
                  />
                  {errors.Salary && (
                    <p style={{ color: "red" }}>{errors.Salary}</p>
                  )}
                </div>
                <div className="mt-4">
                  <input
                    className="btn btn-primary form-control btn-lg"
                    type="submit"
                    value={modalType === "create" ? "Register" : "Save Update"}
                  />
                </div>
              </form>
              {message && (
                <p style={{ color: err ? "red" : "green" }}>{message}</p>
              )}
            </Modal.Body>
            <Modal.Footer>
              <Button
                className="btn btn-danger btn-lg"
                variant="secondary"
                onClick={handleClose}
              >
                Close
              </Button>
            </Modal.Footer>
          </Modal>














    </div>
  );
};

export default ConfirmDeleteModal;
