import React, { useState } from 'react';
import axios from "axios";

function PostJob() {
  const [Company, setCompany] = useState('');
  const [Description, setDescription] = useState('');
  const [Jtitle, setJtitle] = useState('');
  const [Location, setLocation] = useState('');
  const [Salary, setSalary] = useState("");

  const [message, setMessage] = useState(''); // To store success or error message
  const [error, setError] = useState(false);  // To store the error state

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'Company') setCompany(value);
    if (name === 'Description') setDescription(value);
    if (name === 'Jtitle') setJtitle(value);
    if (name === 'Location') setLocation(value);
    if (name === 'Salary') setSalary(value);
  };
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission
  
    const requestBody = {
      Company,
      Description,
      Jtitle,
      Location,
      Salary,
    };
  
    try {
  await axios.post('http://localhost:2000/PostJob', requestBody)
       .then((res)=>{
        if(res.data=="sucess"){}


       })
  
      // The response from axios is already parsed as JSON
      setMessage(response.data.message); // Set success message
      setError(false); // No error
  
      // Optional: Reset form fields after successful submission
      setCompany('');
      setDescription('');
      setJtitle('');
      setLocation('');
      setSalary('');
    } catch (error) {
      // Set a specific error message or a generic one if the server response doesn't contain a message
      const errorMessage = error.response?.data?.message || 'An error occurred. Please try again.';
      setMessage(errorMessage); // Set error message
      setError(true); // Mark that it's an error
    }
  };
  
  return (
    <div className="container bg-light shadow">
      <h2>Post a Job</h2>
      <form onSubmit={handleSubmit} className="row col-md-5">
        <div>
          <label>Company:</label>
          <input
            className="form-control"
            type="text"
            name="Company"
            value={Company}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            className="form-control"
            type="text"
            name="Description"
            value={Description}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Job Title:</label>
          <input
            className="form-control"
            type="text"
            name="Jtitle"
            value={Jtitle}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            className="form-control"
            type="text"
            name="Location"
            value={Location}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Salary:</label>
          <input
            className="form-control"
            type="number"
            name="Salary"
            value={Salary}
            onChange={handleChange}
            required
          />
        </div>
        <div className='mt-4'>
          <button className='btn btn-primary form-control' type="submit">Submit</button>
        </div>
      </form>

      {/* Display success or error message */}
      {message && (
        <p style={{ color: error ? 'red' : 'green' }}>{message}</p>
      )}
    </div>
  );
}

export default PostJob;
