import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';

function SelfCloseAlert ({ showModal })  {


    const [SelfModalOpen, setSelfModalOpen] = useState(false);
    // const [SelfModalClose, setSelfModalClose] = useState(false);
    const handleSelfClose=()=>{
          setSelfModalOpen(true)
    }



  // Automatically close modal after the delay time
  useEffect(() => {
    if (showModal) {
      const timer = setTimeout(() => {
        Close(); // Close the modal
      }, 3000);
      return () => clearTimeout(timer); // Cleanup the timer
    }
  }, [showModal, Close]);

  return (
    <Modal show={showModal} onHide={Close} centered>
      
      <Modal.Body>Job deleted successfully </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={Close}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default SelfCloseAlert;
