import React, { useState } from "react";

const SendSMS = () => {
  const [smsData, setSmsData] = useState({
    from: "",
    to: "",
    message: "",
  });
  const [responseMessage, setResponseMessage] = useState("");
  const [sending, setSending] = useState(false);
  const handleChange = (e) => {
    setSmsData({
      ...smsData,
      [e.target.name]: e.target.value,
    });
  };

  const sendSMS = async (e) => {
    e.preventDefault();
    setSending(true);
    try {
      const response = await fetch("http://localhost:2000/api/send-sms", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(smsData),
      });

      const data = await response.json();
      if (data.success) {
        setResponseMessage("SMS sent successfully!");
        setSending(false);
      } else {
        setResponseMessage(`Failed to send SMS: ${data.error}`);
        setSending(false);
      }
    } catch (error) {
      setResponseMessage(`Error sending SMS: ${error.message}`);
      setSending(false);
    }
  };

  return (
    <div className="container">
      <h2>Send SMS</h2>
      {responseMessage && <p>{responseMessage}</p>}
      <form onSubmit={sendSMS}>
        <div className="my-3">
          <label>From:</label>
          <input
            className="form-control"
            type="text"
            name="from"
            placeholder="+1234567890"
            value={smsData.from}
            onChange={handleChange}
            required
          />
        </div>
        <div className="my-3">
          <label>To:</label>
          <input
            className="form-control"
            type="text"
            name="to"
            placeholder="+1234567890"
            value={smsData.to}
            onChange={handleChange}
            required
          />
        </div>
        <div className="my-3">
          <label>Message:</label>
          <textarea
            className="form-control"
            name="message"
            placeholder="Enter your message here"
            value={smsData.message}
            onChange={handleChange}
            required
          />
        </div>
        <button className="form-control btn btn-primary" type="submit">
          {" "}
          {sending ? "Sending" : "Send SMS"}
        </button>
      </form>

      <div className={`d-flex gap-4  ${classes.message}`}>
       
          <select
            value={font}
            onChange={(e) => setFont(e.target.value)}
            aria-label="Font"
          >
            <option value="Calibri">Calibri</option>
            <option value="Helvetica">Helvetica</option>
            <option value="Arial">Arial</option>
          </select>
       
     
          <select
            value={fontSize}
            onChange={(e) => setFontSize(parseInt(e.target.value))}
            aria-label="Font size"
          >
            {[8, 10, 12, 14, 16, 18, 24, 32].map((size) => (
              <option key={size} value={size}>
                {size}
              </option>
            ))}
          </select>
     
  
          <button
            onClick={toggleBold}
            className="o365button"
            title="Bold"
            aria-pressed={bold}
          >
            {bold ? <b>B</b> : <small>B</small>}
          </button>
   
          <button
            onClick={toggleItalic}
            className="o365button"
            title="Italics"
            aria-pressed={italic}
          >
            {italic ? <i>I</i> : <small>I</small>}
          </button>
 
        <div className="my-3">
          <button
            onClick={toggleUnderline}
            className="o365button"
            title="Underline"
            aria-pressed={underline}
          >
            {underline ? <u>U</u> : <small>U</small>}
          </button>
        </div>
        <div>
          <div className="my-3 d-flex flex-column gap-0">
            <button
              type="button"
              onClick={() => document.getElementById("colorPicker").click()}
              style={{ color: fontColor }}
              aria-label="Select font color"
            >
              A
            </button>

            <input
              id="colorPicker"
              style={{ height: "10px", width: "30px" }}
              type="color"
              value={fontColor}
              onChange={(e) => setFontColor(e.target.value)}
              title="Font color"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default SendSMS;
