import React, { useState } from "react";
// import './register.css'

function Login() {
  const [values, setValues] = useState({
    Username: "",
    Email: "",
    Password: "",
  });

  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState("");
  const [error, setError] = useState({});

  // Handle form input changes
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  // Validate the form fields
  const validate = () => {
    let errors = {};
    if (!values.Username.trim()) {
      errors.Username = "Username is required.";
    }
    if (!values.Email.trim()) {
      errors.Email = "Email is required.";
    }
    if (!values.Password.trim()) {
      errors.Password = "Password is required.";
    }

    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      // No validation errors, proceed to submit the form
      try {
        const response = await fetch("http://localhost:2000/user/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(values),
        });

        const data = await response.json();
        if (response.ok) {
          setMessage("User loged in successfully!");
          setError(false);
        } else {
          setMessage(data.message || "An error occurred.");
          setError(true);
        }
      } catch (err) {
        setMessage("An unexpected error occurred.");
        setError(true);
        console.error(err);
      }
    } else {
      // There are validation errors
      setMessage("Please fix the errors and submit again.");
      setError(true);
    }
  };

  const [showPassword,setVisibility]=useState(false)
  return (
    <div className="container-fluid  login-bg ">
      <div className="container ">
        <div className="card shadow ">
          <div className="card-header">
            <h3 className="text-center">
              <strong>Login here</strong>
            </h3>
          </div>
          <div className="Logincard-body py-5">
            <form
              onSubmit={handleSubmit}
              className="row col-md-6 Logincard-body  align-self-center"
            >
              <div>
                <label>
                  Username<span style={{ color: "red" }}>*</span>:
                </label>
                <input
                  className="form-control form-control-lg"
                  type="text"
                  name="Username"
                  onChange={handleChange}
                />
                {errors.Username && (
                  <p style={{ color: "red" }}>{errors.Username}</p>
                )}
              </div>

              <div>
                <label>
                  Email<span style={{ color: "red" }}>*</span>:
                </label>
                <input
                  className="form-control form-control-lg"
                  type="text"
                  name="Email"
                  onChange={handleChange}
                />
                {errors.Email && <p style={{ color: "red" }}>{errors.Email}</p>}
              </div>

              <div style={{ position: 'relative' }}>
                <label>
                  Password<span style={{ color: "red" }}>*</span>:
                </label>
                <input
                  className="form-control form-control-lg"
                  type={showPassword ? "text":"password"}
                  name="Password"
                  onChange={handleChange}
                />
                <button
                  type="button"
                 
                  style={{
                    position: "absolute",
                    right: "20px",
                    top: "70%",
                    transform: "translateY(-60%)",
                    background: "none",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                 {showPassword ? "Hide" : "Show"}
                  
                </button>

                {errors.Password && (
                  <p style={{ color: "red" }}>{errors.Password}</p>
                )}
              </div>

              <div className="mt-4">
                <button
                  className=" btn-primary form-control form-control-lg"
                  type="submit"
                >
                  Login
                </button>
              </div>
            </form>
            {message && <p style={{ color: errors ? "red" : "green" }}></p>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
