import React,{useState} from 'react'
import { Modal, Button } from "react-bootstrap";
const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:2000/";


const JobModal = ({ modalType, showModal, handleClose, SlectedJob }) => {
  const [formSlectedJob, setFormSlectedJob] = useState({
    Company: "",
    Description: "",
    Jtitle: "",
    Location: "",
    Salary: "",
  });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState("");
  const [err, setError] = useState(false);

  const handleChange = (e) => {
    setFormSlectedJob({ ...formSlectedJob, [e.target.name]: e.target.value });
  };

  const validate = () => {
    let errors = {};
    if (!formValues.Company.trim()) errors.Company = "Company name is required.";
    if (!formValues.Description.trim()) errors.Description = "Description is required.";
    if (!formValues.Jtitle.trim()) errors.Jtitle = "Job title is required.";
    if (!formValues.Location.trim()) errors.Location = "Location is required.";
    if (!formValues.Salary || isNaN(formValues.Salary) || formValues.Salary <= 0)
      errors.Salary = "Salary must be a positive number.";
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      try {
        const url = modalType === "create" 
          ? `${API_BASE_URL}ManageJobs/PostJob` 
          : `${API_BASE_URL}ManageJobs/UpdateJob/${values.JOB_ID}`;
        
        const method = modalType === "create" ? "POST" : "PUT";
        const response = await fetch(url, {
          method: method,
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formValues),
        });

        const data = await response.json();
        if (response.ok) {
          setMessage(modalType === "create" ? "Job posted successfully!" : "Job updated successfully!");
          setError(false);
          setFormValues({
            Company: "",
            Description: "",
            Jtitle: "",
            Location: "",
            Salary: "",
          });
        } else {
          setMessage(data.message || "An error occurred.");
          setError(true);
        }
      } catch (err) {
        setMessage("An unexpected error occurred.");
        setError(true);
        console.error(err);
      }
    } else {
      setMessage("Please fix the errors and submit again.");
      setError(true);
    }
  };

  return (
    <Modal className="modal-top" show={showModal} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>
          {modalType === "create" ? "Post New  Job" : "Update Job"}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={handleSubmit}>
          <div>
            <label>
              Company
              <span style={{ color: "red", fontSize: "30px", marginLeft: "1px" }}>*</span>:
            </label>
            <input className="form-control" type="text" name="Company" value={values.Company} onChange={handleChange} />
            {errors.Company && <p style={{ color: "red" }}>{errors.Company}</p>}
          </div>
          <div>
            <label>Description:</label>
            <input className="form-control" type="text" name="Description" onChange={handleChange} value={values.Description} />
            {errors.Description && <p style={{ color: "red" }}>{errors.Description}</p>}
          </div>
          <div>
            <label>Job Title:</label>
            <input className="form-control" type="text" name="Jtitle" onChange={handleChange} value={values.Jtitle} />
            {errors.Jtitle && <p style={{ color: "red" }}>{errors.Jtitle}</p>}
          </div>
          <div>
            <label>Location:</label>
            <input className="form-control" type="text" name="Location" onChange={handleChange} value={values.Location} />
            {errors.Location && <p style={{ color: "red" }}>{errors.Location}</p>}
          </div>
          <div>
            <label>Salary:</label>
            <input className="form-control" type="number" name="Salary" onChange={handleChange} value={values.Salary} />
            {errors.Salary && <p style={{ color: "red" }}>{errors.Salary}</p>}
          </div>
          <div className="mt-4">
            <input className="btn btn-primary form-control btn-lg" type="submit" value={modalType === "create" ? "Register" : "Save Update"} />
          </div>
        </form>
        {message && <p style={{ color: err ? "red" : "green" }}>{message}</p>}
      </Modal.Body>
      <Modal.Footer>
        <Button className="btn btn-danger btn-lg" variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default JobModal;
