import React, { useEffect, useState } from "react";
// import Video from "./video/Video"; // Uncomment if you need this component

function Index() {
  const [jobsData, setData] = useState([]);
  const [error, setError] = useState("");
  
  useEffect(() => {
    const fetchJobs = async () => {
      const api = "http://localhost:2000/";
      try {
        const response = await fetch(api);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setData(data);
      } catch (e) {
        console.error(e.message);
        setError("Failed to fetch jobs. Please try again later.");
      }
    };

    fetchJobs();
  }, []); // Empty dependency array to run effect once on mount

  if (error) {
    return (
      <section className="container-fluid my-5">
        <h2 style={{ color: "red" }}>{error}</h2>
      </section>
    );
  }

  if (jobsData.length === 0) {
    return (
      <section className="container-fluid my-5">
        <h2>No data found</h2>
      </section>
    );
  }

  return (
    <>
      <section className="container-fluid my-5">
        <div className="search text-center">
          <h2 className="mb-4">Find Your Dream Job</h2>

          <form action="#" method="get">
            <div className="row d-inline-flex gx-5">
              <div className="col-md-3 mb-2">
                <input
                  type="text"
                  className="form-control-lg"
                  name="keywords"
                  placeholder="Keywords"
                />
              </div>
              <div className="col-md-3 mb-2">
                <input
                  type="text"
                  className="form-control-lg"
                  name="location"
                  placeholder="Location"
                />
              </div>
              <div className="col-md-3 mb-2">
                <input
                  type="text"
                  className="form-control-lg"
                  name="company"
                  placeholder="Company"
                />
              </div>
              <div className="col-md-2 mb-2">
                <button type="submit" className="btn btn-primary btn-block">
                  Search
                </button>
              </div>
            </div>
          </form>
        </div>
      </section>
      <section className="container-fluid">
        <div className="job-listings">
          <h2 className="text-center mb-4">Latest Job Listings</h2>
          <hr style={{ width: "100%" }} />

          <div className="row">
            {jobsData?.map((job, index) => (
              <div className="col-md-4" key={index}>
                <div className="card shadow mb-4">
                  <div className="card-header">
                    <h3>
                      <strong>{job.Jtitle}</strong>
                    </h3>
                  </div>
                  <div className="card-body">
                    <p className="card-text">Company: {job.Company}</p>
                    <p className="card-text">Location: {job.Location}</p>
                    <p className="card-text">Salary: {job.Salary}</p>
                    <p className="card-text">Description: {job.Description}</p>
                    <a href="#" className="btn btn-success btn-sm">
                      Apply Now
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <div className="p-5 mb-4 bg-light rounded-3">
        <div className="container-fluid py-5">
          <h1 className="display-5 fw-bold">Custom jumbotron</h1>
          <p className="col-md-8 fs-4">
            Using a series of utilities, you can create this jumbotron, just
            like the one in previous versions of Bootstrap. Check out the
            examples below for how you can remix and restyle it to your liking.
          </p>
          <button className="btn btn-primary btn-lg" type="button">
            Example button
          </button>
        </div>
        {/* <Video /> */}
      </div>
    </>
  );
}

export default Index;
