import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Button, Pagination } from "react-bootstrap";
import ConfirmDeleteModal from "./ConfirmDeleteModal";
import SelfClosingAlert from "./SelfClosingAlert";
import JobModal from "./JobComponent/JobModal";

const ManageJob = () => {
  const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:2000/";

  const [showModal, setShow] = useState(false);
  const [modalType, setModalType] = useState("create");
  const [selectedJob, setSelectedJob] = useState({});
  const [selectedJobID, setSelectedJobID] = useState(null);
  const [jobsData, setJobs] = useState([]);
  const [message, setMessage] = useState("");
  const [selfModalOpen, setSelfModalOpen] = useState(false);

  // Pagination state
  const rowsPerPage = 2;
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(jobsData.length / rowsPerPage);
  const currentData = jobsData.slice(
    (currentPage - 1) * rowsPerPage,
    currentPage * rowsPerPage
  );

  // Fetch jobs data
  useEffect(() => {
    fetch(`${API_BASE_URL}`)
      .then((response) => response.json())
      .then((data) => setJobs(data.Jobs || []))
      .catch((err) => console.error(err.message));
  }, []);

  const changePage = (newPage) => {
    if (newPage > 0 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  const renderPaginationItems = () => {
    const items = [];
    for (let page = 1; page <= totalPages; page++) {
      items.push(
        <Pagination.Item
          key={page}
          active={page === currentPage}
          onClick={() => changePage(page)}
        >
          {page}
        </Pagination.Item>
      );
    }
    return items;
  };

  const handleModalClose = () => {
    setSelectedJob({});
    setShow(false);
  };

  const handleShowPostModal = () => {
    setModalType("create");
    setShow(true);
  };

  const handleShowUpdateModal = (job, id) => {
    setSelectedJob({
      Company: job.COMPANY,
      Description: job.DESCRIPTION,
      Jtitle: job.JOB_TITLE,
      Location: job.LOCATION,
      Salary: job.SALARY,
    });
    setSelectedJobID(id);
    setModalType("update");
    setShow(true);
  };

  // Delete logic
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [jobIdToDelete, setJobIdToDelete] = useState(null);

  const handleDeleteClick = (jobId) => {
    setIsModalOpen(true);
    setJobIdToDelete(jobId);
  };

  const handleCancelDelete = () => setIsModalOpen(false);

  const handleConfirmDelete = async () => {
    setIsModalOpen(false);
    try {
      const response = await fetch(
        `${API_BASE_URL}ManageJobs/DeleteJob/${jobIdToDelete}`,
        { method: "DELETE" }
      );

      if (response.ok) {
        setMessage("Job deleted successfully!");
        setJobs((prevJobs) => prevJobs.filter((job) => job.JOB_ID !== jobIdToDelete));
        setSelfModalOpen(true);
      } else {
        setMessage("Failed to delete the job.");
      }
    } catch (err) {
      console.error(err);
      setMessage("An unexpected error occurred.");
    }
  };

  if (jobsData) {
    return (
      <div className="container-fluid pt-5 " style={{ marginTop: "100px" }}>
        <div className="modal-container bg-light clearfix container">
          <SelfClosingAlert
            show={SelfModalOpen}
            onClose={handleSelfClose}
            message={DeletMessage}
          />
          <Button
            style={{ float: "right" }}
            className="btn btn-success"
            onClick={ShowPostModal}
          >
            Add new Job
          </Button>
        <JobModal
      
          modalType= {modalType}
          showModal={showModal}
        
          handleClose={handleClose}
          SlectedJob={selectedJob}
          id={SlectedJob_ID}
         
        />

          {/* <Modal className="modal-top" show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>
                {modalType === "create" ? "Post a Job" : "Update Job"}
                {selectedJob.JOB_ID}
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={handleSubmit}>
                <div>
                  <label>
                    Company
                    <span
                      style={{
                        color: "red",
                        fontSize: "30px",
                        marginLeft: "1px",
                      }}
                    >
                      *
                    </span>
                    :
                  </label>
                  <input
                    className="form-control"
                    type="text"
                    name="Company"
                    value={values.Company}
                    onChange={handleChange}
                  />
                  {errors.Company && (
                    <p style={{ color: "red" }}>{errors.Company}</p>
                  )}
                </div>
                <div>
                  <label>Description:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Description"
                    onChange={handleChange}
                    value={values.Description}
                  />
                  {errors.Description && (
                    <p style={{ color: "red" }}>{errors.Description}</p>
                  )}
                </div>
                <div>
                  <label>Job Title:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Jtitle"
                    onChange={handleChange}
                    value={values.Jtitle}
                  />
                  {errors.Jtitle && (
                    <p style={{ color: "red" }}>{errors.Jtitle}</p>
                  )}
                </div>
                <div>
                  <label>Location:</label>
                  <input
                    className="form-control"
                    type="text"
                    name="Location"
                    onChange={handleChange}
                    value={values.Location}
                  />
                  {errors.Location && (
                    <p style={{ color: "red" }}>{errors.Location}</p>
                  )}
                </div>
                <div>
                  <label>Salary:</label>
                  <input
                    className="form-control"
                    type="number"
                    name="Salary"
                    onChange={handleChange}
                    value={values.Salary}
                  />
                  {errors.Salary && (
                    <p style={{ color: "red" }}>{errors.Salary}</p>
                  )}
                </div>
                <div className="mt-4">
                  <input
                    className="btn btn-primary form-control btn-lg"
                    type="submit"
                    value={modalType === "create" ? "Register" : "Save Update"}
                  />
                </div>
              </form>
              {message && (
                <p style={{ color: err ? "red" : "green" }}>{message}</p>
              )}
            </Modal.Body>
            <Modal.Footer>
              <Button
                className="btn btn-danger btn-lg"
                variant="secondary"
                onClick={handleClose}
              >
                Close
              </Button>
            </Modal.Footer>
          </Modal> */}
        </div>

        <div className="table-container shadow bg-light container ">
          <table className="table table-striped table-hover">
            <thead style={{backgroundColor:"lightgray"}}>
              <tr>
                <th scope="col">No</th>
                <th scope="col">Job ID</th>
                <th scope="col">Job Title</th>
                <th scope="col">Company</th>
                <th scope="col">Location</th>
                <th scope="col">Salary</th>
                <th scope="col">Description</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody className="table-group-divider">
              {currentData && currentData.length > 0 ? (
                currentData.map((item, index) =>
                  item ? (
                    <tr key={item.JOB_ID}>
                      <td>{(currentPage - 1) * rowsPerPage + index + 1}</td>
                      <td>{item.JOB_ID}</td>
                      <td>{item.JOB_TITLE}</td>
                      <td>{item.COMPANY}</td>
                      <td>{item.LOCATION}</td>
                      <td>{item.SALARY}</td>
                      <td>{item.DESCRIPTION}</td>
                      <td style={{ display: "Flex", columnGap: "10px" }}>
                        <Link className="btn btn-success">View</Link>
                        <button
                          onClick={() => ShowUpdateModal(item,item.JOB_ID)}
                          className="btn btn-warning"
                        >
                          Update
                        </button>
                        <Link
                          onClick={() => handlDeleteClick(item.JOB_ID)}
                          className="btn btn-danger"
                        >
                          Delete
                        </Link>
                      </td>
                    </tr>
                  ) : null
                )
              ) : (
                <tr>
                  <td colSpan="8" className="text-center">
                    No jobs available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>

          <Pagination className="justify-content-center"  size="lg">
          <Pagination.First disabled={true} />
            <Pagination.Prev
                onClick={() => changePage(currentPage - 1)}
                disabled={currentPage === 1}
            >
                Previous
            </Pagination.Prev>

            {renderPaginationItems()}

            <Pagination.Next
                onClick={() => changePage(currentPage + 1)}
                disabled={currentPage === totalPages}
            >
                Next
            </Pagination.Next>
            <Pagination.Last disabled={true} />
        </Pagination>
          <ConfirmDeleteModal 
            isOpen={isModalOpen}
            onConfirm={handleConfirmDelete}
            onCancel={handleCancelDelete}
            message={DeletMessage}
          />

          {/* <SelfCloseModalAlert
            showModal={SelfModalOpen}
            onClose={handleSelfClose}
          
          
          /> */}
        </div>
      </div>
    );
  } else {
    return (
      <section
        className="container-fluid bg-light"
        style={{ marginTop: "100px" }}
      >
        <div className="p-5 mb-4 bg-light rounded-3">
          <div className="container-fluid py-5 text-center">
            <h1 style={{ color: "red" }}>There is no jobs available</h1>
          </div>
        </div>
      </section>
    );
  }
}

export default ManageJob;
