import React ,{ useState } from  "react"
import  {useEffect} from "react"
import "./UsingHook.css"

function UsingHook(){
   const [count,setCount]=useState(0)
   const [age,setAge]=useState()
   const [Name,setName]=useState("")

   useEffect(()=>{
    document.title=`Count:${count}`
   },[count])
   
 return(
<div>
    
    <button onClick={()=>setCount((count)=>count+1)}>Count:{count}</button>
    <button onClick={()=>setCount((count)=>count=0)}>Reset count</button>

    <input type="text" name="age" placeholder="Enter your age.." onChange={e =>setAge(e.target.value)}></input>
    <input type="text" name="name" placeholder="Enter your name.." onChange={(e)=>setName(e.target.value)} ></input>

    <div>
        <h3>your Age:{age}</h3>
        <h3>Your Name: {Name}</h3>

    </div>



</div>



 )
}


export default UsingHook
